pub mod tools {
    // Standard Library Imports
    use std::path;
    use std::env;
    use std::error::Error;
    use sysinfo::System;


    // Guesses Minecraft Directory
    #[allow(dead_code)]
    pub fn guess_minecraft_dir() -> Result<String, Box<dyn Error>> {
            // Checks that Windows is the OS
            let username = env::var("USERNAME")?;

            // Use different environment variables based on the OS
            let guess = format!(r"C:\Users\{username}\AppData\Roaming\.minecraft");

            // Checks existance of directory
            let check = path::Path::new(&guess);
            if check.exists() && check.is_dir() {
                Ok(guess)
            } else {
                Err(format!("Minecraft Directory does not exist at {}", guess).into())
            }
    }

    #[allow(dead_code)]
    pub fn determine_ram_alloc(factor:f64) -> u64 {
        // Determines RAM Allocation for Minecraft based on available computer memory
        // factor: f64, factor to determine what to divide the available RAM by

        // RAM usage determination
        let mut sys = System::new_all();
        sys.refresh_memory();

        // Determines amount of RAM to allocate
        let total_ram_gb = sys.total_memory() as f64 / f64::powf(1024.0, 3.0);
        let mc_alloc = (total_ram_gb.round() / factor) as u64;

        // Returns allocation
        mc_alloc
    }
}